function loadXMLDoc(){fetch("./assets/js/json/certs.xml").then((e=>e.text())).then((e=>{searchBooks((new DOMParser).parseFromString(e,"text/xml"))})).catch((e=>{}))}function searchBooks(e){const t=document.getElementById("searchTerm").value.toLowerCase(),n=e.getElementsByTagName("cert"),o=[];for(let e=0;e<n.length;e++){const a=n[e].getElementsByTagName("id")[0].textContent.toLowerCase();n[e].getElementsByTagName("nombre")[0].textContent.toLowerCase(),n[e].getElementsByTagName("curso")[0].textContent.toLowerCase(),n[e].getElementsByTagName("fecha")[0].textContent.toLowerCase(),n[e].getElementsByTagName("horas")[0].textContent.toLowerCase(),n[e].getElementsByTagName("Instructor")[0].textContent.toLowerCase(),n[e].getElementsByTagName("dc3")[0].textContent.toLowerCase(),n[e].getElementsByTagName("certificado")[0].textContent.toLowerCase(),n[e].getElementsByTagName("negocio")[0].textContent.toLowerCase(),n[e].getElementsByTagName("ciudad")[0].textContent.toLowerCase();a.includes(t)&&o.push({id:n[e].getElementsByTagName("id")[0].textContent,nombre:n[e].getElementsByTagName("nombre")[0].textContent,curso:n[e].getElementsByTagName("curso")[0].textContent,fecha:n[e].getElementsByTagName("fecha")[0].textContent,horas:n[e].getElementsByTagName("horas")[0].textContent,Instructor:n[e].getElementsByTagName("Instructor")[0].textContent,dc3:n[e].getElementsByTagName("dc3")[0].textContent,certificado:n[e].getElementsByTagName("certificado")[0].textContent,negocio:n[e].getElementsByTagName("negocio")[0].textContent,ciudad:n[e].getElementsByTagName("ciudad")[0].textContent})}36===t.length?displayResults(o):alert("No results found.")}function displayResults(e){const t=document.getElementById("resultsTable");for(;t.rows.length>1;)t.deleteRow(1);e.forEach((e=>{document.getElementById("resultado").innerHTML="Resultado",document.getElementById("result_id").innerHTML="ID: "+e.id,document.getElementById("result_nombre").innerHTML="Nombre: "+e.nombre,document.getElementById("result_curso").innerHTML="Programa: "+e.curso,document.getElementById("result_fecha").innerHTML="Fecha concluida: "+e.fecha,document.getElementById("result_horas").innerHTML="Horas totales: "+e.horas,document.getElementById("result_Instructor").innerHTML="Instructor(es): "+e.Instructor+" / #Registro Instructor Externo STPS: LAJA-661002-R85-0005",document.getElementById("result_dc3").innerHTML="DC-3: "+e.dc3,document.getElementById("result_certificado").innerHTML="Certificado: "+e.certificado,document.getElementById("result_negocio").innerHTML="Negocio/Empresa: "+e.negocio,document.getElementById("result_ciudad").innerHTML="Ciudad: "+e.ciudad})),0===e.length&&alert("No results found.")}